import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./aatish.css";

const AatishGPTPage = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const navigate = useNavigate();

  const handleSendMessage = async () => {
    if (!input.trim()) return;

    // Add user message
    const userMessage = { text: input, sender: "user" };
    const updatedMessages = [...messages, userMessage];
    setMessages(updatedMessages);
    setInput("");

    try {
      // Send message to backend
      const response = await axios.post("http://localhost:5000/chat", {
        messages: updatedMessages,
      });

      // Add ChatGPT's response
      setMessages((prev) => [...prev, { text: response.data.reply, sender: "chatgpt" }]);
    } catch (error) {
      console.error("Error fetching AI response:", error);
    }
  };

  return (
    <div className="outer-container">
      {/* Clickable Logo */}
      <img
        src={`${process.env.PUBLIC_URL}/yacht gpt logo.png`}
        alt="Yacht GPT Logo"
        className="logo"
        onClick={() => navigate("/models")}
      />

      {/* Title & Quote */}
      <div className="header">
        <h1 className="title">AATISH-GPT</h1>
        <p className="subtitle">FAVORITE DESSERT: "ROCK"</p>
      </div>

      {/* Chat Section */}
      <div className="chat-section">
        <div className="chatbox">
          {messages.map((msg, index) => (
            <div key={index} className={`message ${msg.sender}`}>
              {msg.text}
            </div>
          ))}
        </div>

        {/* Input + Send Button */}
        <div className="input-container">
          <textarea
            placeholder="Type something..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && handleSendMessage()}
          />
          <button onClick={handleSendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default AatishGPTPage;
