import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./aatish.css";

const AatishGPTPage = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  useEffect(() => {
    axios.get("http://localhost:5000/chat-history", {
      headers: { Authorization: `Bearer ${token}` }
    }).then(res => setMessages(res.data));
  }, [token]);

  const handleSendMessage = async () => {
    if (!input.trim()) return;

    const newMessage = { text: input, sender: "user" };
    setMessages([...messages, newMessage]);
    setInput("");

    try {
      const response = await axios.post("http://localhost:5000/chat", {
        messages: [...messages, newMessage]
      }, { headers: { Authorization: `Bearer ${token}` } });

      setMessages((prev) => [...prev, { text: response.data.reply, sender: "chatgpt" }]);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="chat-container">
      <button onClick={() => navigate("/models")}>Back</button>
      <div className="chatbox">
        {messages.map((msg, index) => <p key={index} className={msg.sender}>{msg.text}</p>)}
      </div>
      <input value={input} onChange={(e) => setInput(e.target.value)} onKeyDown={(e) => e.key === "Enter" && handleSendMessage()} />
      <button onClick={handleSendMessage}>Send</button>
    </div>
  );
};

export default AatishGPTPage;
